import { Component, type OnInit, ViewChild, ElementRef, AfterViewInit } from "@angular/core"
import { CommonModule } from "@angular/common"
import { Router } from "@angular/router"
import { AuthService } from "../../services/auth.service"
import { CampaignService } from '../../services/campaign.service';
import { Chart, registerables } from "chart.js";
Chart.register(...registerables);

@Component({
  selector: "app-dashboard",
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="min-h-screen bg-gray-50">
      <!-- Header -->
      <header class="bg-white shadow-sm border-b border-gray-200">
        <div class="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div class="flex justify-between items-center h-16">
            <div class="flex items-center">
              <div class="flex-shrink-0">
                <div class="inline-flex items-center justify-center w-8 h-8 bg-blue-600 rounded-lg">
                  <svg class="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M3 8l7.89 4.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z"></path>
                  </svg>
                </div>
              </div>
              <div class="ml-4">
                <h1 class="text-xl font-semibold text-gray-900">Good Mail Dashboard</h1>
              </div>
            </div>
            <div class="flex items-center space-x-4">
              <span class="text-sm text-gray-700">Welcome back!</span>
              <button
                (click)="logout()"
                class="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition-colors"
              >
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      <!-- Main Content -->
      <main class="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div class="px-4 py-6 sm:px-0">
          <div *ngIf="stats; else emptyDashboard">
            <div class="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
              <div class="bg-white rounded-lg shadow p-6 text-center">
                <div class="text-2xl font-bold text-blue-600">{{ stats.emailsSent }}</div>
                <div class="text-gray-500 mt-2">Emails sent</div>
              </div>
              <div class="bg-white rounded-lg shadow p-6 text-center">
                <div class="text-2xl font-bold text-orange-600">{{ stats.clicks }}</div>
                <div class="text-gray-500 mt-2">Clicks in emails</div>
              </div>
              <div class="bg-white rounded-lg shadow p-6 text-center">
                <div class="text-2xl font-bold text-green-600">{{ stats.revenue | currency }}</div>
                <div class="text-gray-500 mt-2">Email revenue</div>
              </div>
              <div class="bg-white rounded-lg shadow p-6 text-center">
                <div class="text-2xl font-bold text-purple-600">{{ (stats.growth * 100) | number:'1.0-2' }}%</div>
                <div class="text-gray-500 mt-2">Growth rate</div>
              </div>
            </div>
            <div class="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
              <div class="bg-white rounded-lg shadow p-6">
                <div class="text-lg font-medium mb-2">Main Metrics</div>
                <div class="text-2xl font-bold text-gray-900 mb-1">{{ stats.revenue | currency }}</div>
                <div class="text-sm text-gray-500 mb-4">+37% vs last month</div>
                <canvas #mainMetricsChart></canvas>
              </div>
              <div class="bg-white rounded-lg shadow p-6">
                <div class="text-lg font-medium mb-2">Your campaigns</div>
                <canvas #campaignsBarChart></canvas>
              </div>
            </div>
          </div>
          <ng-template #emptyDashboard>
            <div class="border-4 border-dashed border-gray-200 rounded-lg p-8 text-center">
              <div class="max-w-md mx-auto">
                <svg class="mx-auto h-12 w-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z"></path>
                </svg>
                <h3 class="mt-4 text-lg font-medium text-gray-900">Email Campaign Dashboard</h3>
                <p class="mt-2 text-sm text-gray-500">
                  Welcome to your email campaign management dashboard. This is where you'll manage your campaigns, track analytics, and grow your audience.
                </p>
                <div class="mt-6">
                  <button class="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg font-medium transition-colors" (click)="createCampaign()">
                    Create Your First Campaign
                  </button>
                </div>
              </div>
            </div>
          </ng-template>
        </div>
      </main>
    </div>
  `,
  styleUrls: ["./dashboard.component.css"],
})
export class DashboardComponent implements OnInit, AfterViewInit {
  stats: any = null;
  @ViewChild('mainMetricsChart') mainMetricsChartRef!: ElementRef;
  @ViewChild('campaignsBarChart') campaignsBarChartRef!: ElementRef;

  constructor(
    private authService: AuthService,
    private campaignService: CampaignService,
    private router: Router,
  ) {}

  ngOnInit(): void {
    if (!this.authService.isAuthenticated()) {
      this.router.navigate(["/login"])
    } else {
      this.campaignService.getDashboardStats().subscribe((res) => {
        this.stats = res?.data;
        setTimeout(() => this.initCharts(), 0);
      });
    }
  }

  ngAfterViewInit(): void {
    this.initCharts();
  }

  initCharts(): void {
    this.createMainMetricsChart();
    this.createCampaignsBarChart();
  }

  createMainMetricsChart(): void {
    if (!this.mainMetricsChartRef) return;
    const ctx = this.mainMetricsChartRef.nativeElement.getContext('2d');
    new Chart(ctx, {
      type: 'line',
      data: {
        labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'],
        datasets: [{
          label: 'Email Revenue',
          data: [1200, 2500, 4000, 6000, 8000, 10000, 12000, 13500, 14000, 14500, 15000, 15300],
          borderColor: '#4ade80',
          backgroundColor: 'rgba(74, 222, 128, 0.1)',
          tension: 0.4,
          fill: true,
        }],
      },
      options: {
        responsive: true,
        plugins: { legend: { display: false } },
        scales: { x: { grid: { display: false } }, y: { beginAtZero: true } },
      },
    });
  }

  createCampaignsBarChart(): void {
    if (!this.campaignsBarChartRef) return;
    const ctx = this.campaignsBarChartRef.nativeElement.getContext('2d');
    new Chart(ctx, {
      type: 'bar',
      data: {
        labels: ['Campaign 1', 'Campaign 2', 'Campaign 3', 'Campaign 4', 'Campaign 5', 'Campaign 6'],
        datasets: [
          { label: 'Open rate', data: [42, 48, 38, 50, 45, 47], backgroundColor: '#a3e635' },
          { label: 'CTR', data: [16, 24, 12, 20, 18, 22], backgroundColor: '#60a5fa' },
          { label: 'Bounce rate', data: [2, 1, 3, 2, 2, 1], backgroundColor: '#f87171' },
        ],
      },
      options: {
        responsive: true,
        plugins: { legend: { position: 'top' } },
        scales: { x: { grid: { display: false } }, y: { beginAtZero: true } },
      },
    });
  }

  logout(): void {
    this.authService.logout()
    this.router.navigate(["/login"])
  }

  createCampaign() {
    this.router.navigate(["/campaigns/new"]);
  }
}
